def make_dir(path):
    if(os.path.exists(path) and os.path.isdir(path)):
        pass
    else:
        os.makedirs(path)


def get_r_script():
    r_script = os.path.join(os.path.dirname(__file__), "R/go_enrichment_v2.R")
    return r_script


def EnrichmentParser(subparsers):
    workflow = subparsers.add_parser("Enrichment", help = "Perform go enrichment by clusterProfiler")
    group_input = workflow.add_argument_group("input arguments")
    group_input.add_argument('--Rscript', type=str, required=True, default=None, help='Rscript path (default: %(default)s)')
    group_input.add_argument('--input', dest='input', type=str, required=True, default=None, help='Input markerGene filename which generated by Seurat workflow.')
    group_input.add_argument('--species', dest='species', type=str, required=True, default='Human', choices=['Human', 'Mouse'], help='Species name')
    group_input.add_argument('--cluster', dest='cluster', type=str, required=True, default=None, help='Cluser file name to compare go enrichment (one column)')
    
    group_output = workflow.add_argument_group("output arguments")
    group_output.add_argument('--out',type=str,required=True,default=None,help='Directory to save file')


def Enrichment(args):
    make_dir(args.out)
    r_script = get_r_script()
    commands = [args.Rscript, r_script]
    
    rscript_args = [
        "--input", args.input,
        "--species", args.species,
        "--cluster", args.cluster,
        "--out", args.out]

    commands.extend(rscript_args)
    subprocess.run([str(i) for i in commands])


if __name__ == "__main__":
    parser = argparse.ArgumentParser(prog='ScAT', description='ScAT - a python xxxxxxxxxxxxxxxxxx',
                                  epilog='Use %(prog)s {command} -h to get help on individual commands')

    subparsers = parser.add_subparsers(dest = "subcommand")
    EnrichmentParser(subparsers)
    args = parser.parse_args()
    Enrichment(args)